import React, { Component } from "react";
import "../App.css";
import { Button, Form, FormGroup, Label, Input, Col } from "reactstrap";
import DisplayTable from "../Components/DisplayTable";

class FormR extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      name: "",
      id: "",
      place: "",
      party: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value,
    });
    console.log("name", event.target.name);
  };

  handleDelete = (i) => {
    let list = this.state.list.slice();
    list.splice(i, 1);
    this.setState({
      list,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { name, id, place, party } = this.state;

    let newState = {
      name,
      id,
      place,
      party,
    };
    if (name && id && place && party !== "") {
      this.setState({
        list: [...this.state.list, newState],
      });
    }
    this.setState({
      name: "",
      id: "",
      place: "",
      party: "",
    });
  };

  render() {
    const { name, id, place, party } = this.state;
    return (
      <div>
        <div className="formBody">
          <h1 style={{ textAlign: "center" }}>Voting Registration Form</h1>
          <div className="side1"></div>
          <Form className="form" onSubmit={this.handleSubmit}>
            <FormGroup row>
              <Label for="exampleName" sm={2}>
                Name
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="name"
                  placeholder="Enter your Name"
                  value={name}
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="exampleId" sm={2}>
                Id Number
              </Label>
              <Col sm={10}>
                <Input
                  type="number"
                  name="id"
                  placeholder="Enter your Id Number"
                  value={id}
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="examplePlace" sm={2}>
                Place
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="place"
                  placeholder="Enter place of vote"
                  value={place}
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>

            <FormGroup
              tag="fieldset"
              row
              value={party}
              onChange={this.handleChange}
            >
              <legend style={{ padding: "2rem" }}>
                South African Parliamentary parties
              </legend>
              <Col sm={10}>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="ANC" />
                    African National Congress{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="DA" />
                    Democratic Alliance
                  </Label>
                </FormGroup>

                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="EFF" />

                    Economic Freedom Fighters{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="IFP" />

                    Inkatha Freedom Party{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="FFP" />
                    Freedom Front Plus{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="ACDP"/>
                    African Christian Democratic Party
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party"  value="UDM"/>
                    United Democratic Movement
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="ATM" />

                    African Transformation Movement{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="GOOD" />
                    Good{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="NFP" />

                    National Freedom Party{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="AIC" />
                    African Independent Congress{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="COPE" />

                    Congress of the People{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="PAC" />
                    Pan Africanist Congress of Azania{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="ALJAMA" />
                    Al Jama-ah
                  </Label>
                </FormGroup>
              </Col>
            </FormGroup>

            <FormGroup check row>
              <Col sm={{ size: 10, offset: 2 }}>
              <Button color="success">Register</Button>{' '}
              </Col>
            </FormGroup>
          </Form>
          </div>
          <hr />
          <div>
          <DisplayTable
            list={this.state.list}
            handleDelete={this.handleDelete}
          />
        </div>
      </div>
    );
  }
}

export default FormR;
