import React, { Component } from "react";
import "../App.css";
import { Button, Form, FormGroup, Label, Input, Col } from "reactstrap";
import DisplayTable from "../Components/DisplayTable";

class FormR extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      name: "",
      id: "",
      place: "",
      party: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value,
    });
    console.log("name", event.target.name);
  };

  handleDelete = (i) => {
    let list = this.state.list.slice();
    list.splice(i, 1);
    this.setState({
      list,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { name, id, place, party } = this.state;

    let newState = {
      name,
      id,
      place,
      party,
    };
    if (name && id && place && party !== "") {
      this.setState({
        list: [...this.state.list, newState],
      });
    }
    this.setState({
      name: "",
      id: "",
      place: "",
      party: "",
    });
  };

  render() {
    const { name, id, place, party } = this.state;
    return (
      <div>
        <div className="formBody">
          <h1 style={{ textAlign: "center" }}>Voting Registration Form</h1>
          <Form className="formBody" onSubmit={this.handleSubmit}>
            <FormGroup row>
              <Label for="exampleName" sm={2}>
                Name
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="name"
                  placeholder="Enter your Name"
                  value={name}
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="exampleId" sm={2}>
                Id Number
              </Label>
              <Col sm={10}>
                <Input
                  type="number"
                  name="id"
                  placeholder="Enter your Id Number"
                  value={id}
                  onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
