import React, { Component } from "react";
import "../App.css";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  Col,
} from "reactstrap";
import DisplayTable from "../Components/DisplayTable"


class FormR extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      name: "",
      id: "",
      place: "",
      party: "",
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
}

handleChange = (event) => {
    this.setState({
        ...this.state,
        [event.target.name]: event.target.value,
    });
    console.log("name", event.target.name)
  };


  handleSubmit = (event) => {
    event.preventDefault();
    const { name, id, place, party } = this.state;

    let newState = {
        name,
         id,
        place,
        party,
      };
      console.log("tumi", newState.name);
      this.setState({
        list: [...this.state.list, newState]
      });
      this.setState({
        name: "",
        id: "",
        place: "",
        party: "",
      });
  }


  render() {
      const { name , id, place, party } = this.state
    return (
      <div>
        <div className="formBody">
          <h1 style={{textAlign: "center"}}>Voting Registration Form</h1>
          <Form className="formBody" onSubmit={this.handleSubmit}>
          <FormGroup row>
              <Label for="exampleName" sm={2}>
                Name
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="name"
                  placeholder="Enter your Name"
                  value={name} onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="exampleId" sm={2}>
                Id Number
              </Label>
              <Col sm={10}>
                <Input
                  type="number"
                  name="id"
                  placeholder="Enter your Id Number"
                  value={id} onChange={this.handleChange}
                />
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="examplePlace" sm={2}>
                Place
              </Label>
              <Col sm={10}>
                <Input
                  type="text"
                  name="place"
                  placeholder="Enter place of vote"
                  value={place} onChange={this.handleChange}
                />
              </Col>
            </FormGroup>

            <FormGroup tag="fieldset" row  value={party}
    onChange={this.handleChange}>
              <legend style={{padding: "2rem"}}>Sout African Parliamentary parties</legend>
              <Col sm={10}>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="ANC"  /> African National
                    Congress{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="DA"  /> Democratic Alliance{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>

                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value="EFF"  /> Economic Freedom
                    Fighters{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Inkatha Freedom Party{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Freedom Front Plus{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> African Christian
                    Democratic Party{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> United Democratic
                    Movement{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> African Transformation
                    Movement{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Good{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> National Freedom Party{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> African Independent
                    Congress{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Congress of the People{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Pan Africanist Congress
                    of Azania{" "}
                  </Label>
                </FormGroup>
                <FormGroup check>
                  <Label check>
                    <Input type="radio" name="party" value={party}  /> Al Jama-ah{" "}
                  </Label>
                </FormGroup>
              </Col>
            </FormGroup>

            <FormGroup check row>
              <Col sm={{ size: 10, offset: 2 }}>
                <Button>Submit</Button>
              </Col>
            </FormGroup>
          </Form>
          <hr/>
          <DisplayTable list={this.state.list} />

        </div>
      </div>
    );
  }
}

export default FormR;
